import React from 'react';
import '../../assets/styles/Login.css';
import Header from '../common/Header';
import Footer from '../common/Footer';
import Side from '../../assets/images/side2.jpg';
import SignupBtn from '../common/SignupBtn';

class Login extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            email: "",
            password: ""
        };
    }

    handleValidation = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        this.setState({[name]: value});

    }

    render() {
      return (
        <div className=".mx-auto px-0">

            <div>
                <Header/>
            </div>
            <div className="row index">
                <div className="col-md-4">
                    <SignupBtn/>
                    <img className="index" alt="sideBar" src={Side}/>
                </div>
                <div className="col-md-8">
                    <form className="needs-validation position" novalidate>

                        <div className="mb-4">
                            <label htmlFor="loginMail">Email</label>
                            <input type="email" className="form-control" id="loginMail" name="logmail" placeholder="user@domain.com" required/>
                            <div className="invalid-feedback">
                                invalid or empty email address
                            </div>
                        </div>

                        <div className="mb-4">
                            <label htmlFor="loginPassword">Password</label>
                            <input type="password" className="form-control" id="loginPassword" name="logpass" placeholder="password" aria-describedby="passwordHelp" required/>
                            <small id="passwordHelp" class="text-muted">
                                Must be 8-20 characters long, has uppercase and special character.
                            </small>
                            <div className="invalid-feedback">
                                Enter your password
                            </div>
                        </div>

                        <div className="mb-4">
                            <div className="form-check mb-2">
                                <input className="form-check-input" type="checkbox" id="autoSizingCheck"/>
                                <label className="form-check-label" htmlFor="autoSizingCheck">
                                    Remember me
                                </label>
                            </div>
                        </div>

                        <div className="mb-4 ">
                            <div className="col-sm-10 btnpos">
                                <button type="submit" className="btn btn-primary">Submit</button>
                            </div>
                        </div>

                    </form>

                </div>            
            </div>
            <div>
                <Footer/>
            </div>

        </div>
      );
    }
}
export default Login;
