import React from 'react';
import '../../assets/styles/Login.css';
import Header from '../common/Header';
import Footer from '../common/Footer';
import Side from '../../assets/images/side2.jpg';
import SignupBtn from '../common/SignupBtn';
import FormValidator from '../common/FormValidator';

class Login extends React.Component{
    constructor(){
        super();

        this.validator = new FormValidator([
            { 
              field: 'loginmail', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Email is required.' 
            },
            { 
              field: 'loginmail',
              method: 'isEmail', 
              validWhen: true, 
              message: 'That is not a valid email.'
            },
            { 
              field: 'loginpass', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Password is required.'
            },
        ]);

        this.state = {
            email: "",
            password: "",
            formErrors: {email: "", password:""},
            emailValid: false,
            passwordValid: false,
            formValid: false
        };
    }

    handleValidation = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        this.setState({[name]: value});

    }

    render() {
        let validation = this.submitted ?                         // if the form has been submitted at least once
                      this.validator.validate(this.state) :   // then check validity every time we render
this.state.validation 
      return (
        <div className=".mx-auto px-0">

            <div>
                <Header/>
            </div>
            <div className="row index">
                <div className="col-md-4">
                    <SignupBtn/>
                    <img className="index" alt="sideBar" src={Side}/>
                </div>
                <div className="col-md-8">
                    <form className="needs-validation position" novalidate>

                        <div className="mb-4">
                            <label htmlFor="loginMail">Email</label>
                            <input type="email" 
                                className="form-control" 
                                id="loginMail" 
                                name="loginmail" 
                                placeholder="user@domain.com" required
                            />
                            <span className="help-block">{validation.email.message}</span>
                            <div className="invalid-feedback">
                                invalid or empty email address
                            </div>
                        </div>

                        <div className="mb-4">
                            <label htmlFor="loginPassword">Password</label>
                            <input type="password" 
                                className="form-control" 
                                id="loginPassword" 
                                name="loginpass" 
                                placeholder="password" required
                            />
                            <div className="invalid-feedback">
                                Enter your password
                            </div>
                        </div>

                        <div className="mb-4">
                            <div className="form-check mb-2">
                                <input className="form-check-input" type="checkbox" id="autoSizingCheck"/>
                                <label className="form-check-label" htmlFor="autoSizingCheck">
                                    Remember me
                                </label>
                            </div>
                        </div>

                        <div className="mb-4 ">
                            <div className="col-sm-10 btnpos">
                                <button type="submit" className="btn btn-primary">Submit</button>
                            </div>
                        </div>

                    </form>

                </div>            
            </div>
            <div>
                <Footer/>
            </div>

        </div>
      );
    }
}
export default Login;
