import React from 'react';
import '../../assets/styles/Login.css';
import Header from '../common/Header';
import Footer from '../common/Footer';
import Side from '../../assets/images/side2.jpg';
import SignupBtn from '../common/SignupBtn';
import FormValidator from '../common/FormValidator';

class Login extends React.Component{
    constructor(){
        super();

        this.validator = new FormValidator([
            { 
              field: 'loginmail', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Email is required.' 
            },
            { 
              field: 'loginmail',
              method: 'isEmail', 
              validWhen: true, 
              message: 'That is not a valid email.'
            },
            { 
              field: 'loginpass', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Password is required.'
            },
        ]);

        this.state = {
            loginmail: '',
            loginpass: '',
            validation: this.validator.valid(),
        };

        this.submitted = false;
    }

    passwordMatch = (confirmation, state) => (state.password === confirmation);

    handleInputChange = event => {
        event.preventDefault();

        this.setState({
            [event.target.name]: event.target.value,
        });
    }

    handleFormSubmit = event => {
        event.preventDefault();

        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {
        // handle actual form submission here
        }
    }

    render() {

        let validation = this.submitted ? this.validator.validate(this.state) : this.state.validation;     
        return(
        <div className=".mx-auto px-0">

            <div>
                <Header/>
            </div>
            <div className="row index">
                <div className="col-md-4">
                    <SignupBtn/>
                    <img className="index" alt="sideBar" src={Side}/>
                </div>
                <div className="col-md-8">
                    <form className="needs-validation position" novalidate>

                            {/* <div className={validing.email.isInvalid && 'has-error'}> */}
                        <div className={`mb-4 ${validation.loginmail.isInValid && 'has-error'}`}>
                            <label htmlFor="loginMail">Email</label>
                            <input type="email" 
                                className="form-control" 
                                id="loginMail" 
                                name="loginmail" 
                                onChange={this.handleInputChange}
                                placeholder="user@domain.com" required
                            />
                            <span className="help-block">{validation.loginmail.message}</span>
                            <div className="invalid-feedback">
                                invalid or empty email address
                            </div>
                        </div>

                        <div className={`mb-4 ${validation.loginpass.isInValid && 'has-error'}`}>
                            <label htmlFor="loginPassword">Password</label>
                            <input type="password" 
                                className="form-control" 
                                id="loginPassword" 
                                name="loginpass" 
                                onChange={this.handleInputChange}
                                placeholder="password" required
                            />
                            <span className="help-block">{validation.loi.message}</span>
                            <div className="invalid-feedback">
                                Enter your password
                            </div>
                        </div>

                        <div className="mb-4">
                            <div className="form-check mb-2">
                                <input className="form-check-input" type="checkbox" id="autoSizingCheck"/>
                                <label className="form-check-label" htmlFor="autoSizingCheck">
                                    Remember me
                                </label>
                            </div>
                        </div>

                        <div className="mb-4 ">
                            <div className="col-sm-10 btnpos">
                                <button type="submit" className="btn btn-primary">Submit</button>
                            </div>
                        </div>

                    </form>

                </div>            
            </div>
            <div>
                <Footer/>
            </div>

        </div>
      );
    }
}
export default Login;
